import { useState } from 'react';

const BugForm = ({ onSubmit, onCancel, loading }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    priority: 'medium',
    status: 'open',
    environment: {
      os: '', // This field is required by your API
      browser: '',
      version: ''
    },
    stepsToReproduce: '',
    expectedBehavior: '',
    actualBehavior: ''
  });

  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }
    
    if (!formData.description.trim()) {
      newErrors.description = 'Description is required';
    }
    
    if (!formData.environment.os) {
      newErrors.os = 'Operating system is required';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('ðŸš€ Submitting form data:', formData);
    
    if (!validateForm()) {
      console.log('âŒ Form validation failed');
      return;
    }

    try {
      await onSubmit(formData);
      // Form will be closed by parent component on success
    } catch (err) {
      console.error('âŒ Form submission error:', err);
      // Error is handled by parent component
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name.startsWith('environment.')) {
      const envField = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        environment: {
          ...prev.environment,
          [envField]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  return (
    <div className="bug-form-overlay">
      <div className="bug-form-container">
        <h3>Report New Bug</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="title">Title *</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              className={errors.title ? 'error' : ''}
            />
            {errors.title && <span className="error-text">{errors.title}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="description">Description *</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="3"
              className={errors.description ? 'error' : ''}
            />
            {errors.description && <span className="error-text">{errors.description}</span>}
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="priority">Priority</label>
              <select
                id="priority"
                name="priority"
                value={formData.priority}
                onChange={handleChange}
              >
                <option value="low">Low</option>
                <option value="medium">Medium</option>
                <option value="high">High</option>
                <option value="critical">Critical</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="os">Operating System *</label>
              <select
                id="os"
                name="environment.os"
                value={formData.environment.os}
                onChange={handleChange}
                className={errors.os ? 'error' : ''}
              >
                <option value="">Select OS</option>
                <option value="windows">Windows</option>
                <option value="macos">macOS</option>
                <option value="linux">Linux</option>
                <option value="android">Android</option>
                <option value="ios">iOS</option>
                <option value="other">Other</option>
              </select>
              {errors.os && <span className="error-text">{errors.os}</span>}
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="browser">Browser (Optional)</label>
            <input
              type="text"
              id="browser"
              name="environment.browser"
              value={formData.environment.browser}
              onChange={handleChange}
              placeholder="e.g., Chrome, Firefox"
            />
          </div>

          <div className="form-group">
            <label htmlFor="stepsToReproduce">Steps to Reproduce</label>
            <textarea
              id="stepsToReproduce"
              name="stepsToReproduce"
              value={formData.stepsToReproduce}
              onChange={handleChange}
              rows="3"
              placeholder="1. Go to...&#10;2. Click on...&#10;3. Observe..."
            />
          </div>

          <div className="form-actions">
            <button 
              type="button" 
              onClick={onCancel}
              disabled={loading}
            >
              Cancel
            </button>
            <button 
              type="submit" 
              disabled={loading}
              className="btn-primary"
            >
              {loading ? 'Submitting...' : 'Submit Bug Report'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BugForm;
