import React from 'react';
import Button from './Button';

const BugList = ({ bugs, onEdit, onDelete }) => {
  if (bugs.length === 0) {
    return (
      <div className="empty-state">
        <h3>No bugs reported yet</h3>
        <p>Be the first to report a bug and help improve the application!</p>
      </div>
    );
  }

  return (
    <div className="bug-list">
      {bugs.map((bug) => (
        <div key={bug._id} className="bug-item">
          <div className="bug-header">
            <div className="bug-title">{bug.title}</div>
            <div className="bug-meta">
              <span className={`bug-priority bug-priority-${bug.priority}`}>
                {bug.priority}
              </span>
              <span className={`bug-status bug-status-${bug.status}`}>
                {bug.status}
              </span>
            </div>
          </div>
          
          <div className="bug-description">
            {bug.description}
          </div>
          
          {bug.stepsToReproduce && bug.stepsToReproduce.length > 0 && (
            <div className="bug-steps">
              <strong>Steps to reproduce:</strong>
              <ol>
                {bug.stepsToReproduce.map((step, index) => (
                  <li key={index}>{step}</li>
                ))}
              </ol>
            </div>
          )}
          
          {bug.environment && (bug.environment.os || bug.environment.browser) && (
            <div className="bug-environment">
              <strong>Environment:</strong>
              <span>
                {[bug.environment.os, bug.environment.browser, bug.environment.version]
                  .filter(Boolean)
                  .join(' ‚Ä¢ ')}
              </span>
            </div>
          )}
          
          <div className="bug-footer">
            <div className="bug-reporter">
              Reported by {bug.reporter} ‚Ä¢ {new Date(bug.createdAt).toLocaleDateString()}
              {bug.assignee && ` ‚Ä¢ Assigned to ${bug.assignee}`}
            </div>
            
            <div className="bug-actions">
              <Button
                variant="secondary"
                size="sm"
                onClick={() => onEdit(bug)}
              >
                ‚úèÔ∏è Edit
              </Button>
              <Button
                variant="danger"
                size="sm"
                onClick={() => onDelete(bug._id)}
              >
                üóëÔ∏è Delete
              </Button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default BugList;
