import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
});

// Request interceptor for logging
api.interceptors.request.use(
  (config) => {
    console.log(`Making ${config.method.toUpperCase()} request to: ${config.url}`);
    return config;
  },
  (error) => {
    console.error('❌ Request error:', error);
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => {
    console.log('✅ API Response:', response.data);
    return response.data;
  },
  (error) => {
    console.error('❌ API Error:', error.response?.data || error.message);
    
    if (error.response?.status === 400) {
      const validationErrors = error.response.data.errors || error.response.data.message;
      throw new Error(validationErrors || 'Validation failed');
    }
    
    if (error.response?.status === 500) {
      throw new Error('Server error. Please try again later.');
    }
    
    if (error.code === 'ECONNABORTED') {
      throw new Error('Request timeout. Please check your connection.');
    }
    
    throw new Error(error.response?.data?.message || 'Network error. Please try again.');
  }
);

export const getBugs = async () => {
  try {
    return await api.get('/bugs');
  } catch (error) {
    console.error('Error fetching bugs:', error);
    throw error;
  }
};

export const createBug = async (bugData) => {
  try {
    // Ensure environment.os is provided
    if (!bugData.environment?.os) {
      throw new Error('environment.os is required');
    }
    
    return await api.post('/bugs', bugData);
  } catch (error) {
    console.error('Error creating bug:', error);
    throw error;
  }
};
