import React, { useState, useEffect } from 'react';
import BugForm from '../components/BugForm';
import BugList from '../components/BugList';
import Button from '../components/Button';
import { getBugs, createBug, updateBug, deleteBug } from '../services/bugService';
import '../App.css';

function BugTracker() {
  const [bugs, setBugs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [editingBug, setEditingBug] = useState(null);

  useEffect(() => {
    console.log('üîÑ BugTracker mounted, loading bugs...');
    loadBugs();
  }, []);

  const loadBugs = async () => {
    try {
      console.log('üì° Fetching bugs from API...');
      setLoading(true);
      setError(null);
      const response = await getBugs();
      console.log('‚úÖ API Response:', response);
      
      // Ensure we always have an array
      const bugsArray = response?.bugs || response || [];
      console.log('üêõ Bugs array:', bugsArray);
      
      setBugs(Array.isArray(bugsArray) ? bugsArray : []);
    } catch (err) {
      console.error('‚ùå Error loading bugs:', err);
      setError('Failed to load bugs. Please try again.');
    } finally {
      setLoading(false);
      console.log('üèÅ Loading completed');
    }
  };

  // ... rest of your component code remains the same

  return (
    <div className="App">
      <header className="header">
        <div className="container">
          <h1>üêõ MERN Bug Tracker</h1>
          <p>Track, manage, and resolve bugs efficiently</p>
        </div>
      </header>

      <main className="container">
        {error && (
          <div className="error-message submit-error">
            {error}
            <Button 
              variant="secondary" 
              size="sm" 
              onClick={() => setError(null)}
              style={{ marginLeft: '1rem' }}
            >
              Dismiss
            </Button>
          </div>
        )}

        <div className="actions-bar">
          <Button
            variant="primary"
            onClick={() => {
              console.log('üìù Toggling form, current showForm:', !showForm);
              setShowForm(!showForm);
            }}
          >
            {showForm ? 'üìã View Bugs' : 'üêõ Report New Bug'}
          </Button>
          
          <Button
            variant="secondary"
            onClick={() => {
              console.log('üîÑ Refreshing bugs...');
              loadBugs();
            }}
            disabled={loading}
          >
            üîÑ Refresh
          </Button>

          {/* Add filter buttons */}
          <div className="filter-buttons">
            <Button
              variant="secondary"
              size="sm"
              onClick={() => console.log('Filter by status')}
            >
              Filter by Status
            </Button>
            <Button
              variant="secondary"
              size="sm"
              onClick={() => console.log('Filter by priority')}
            >
              Filter by Priority
            </Button>
          </div>
        </div>

        {showForm ? (
          <div className="form-section">
            <h2>{editingBug ? 'Edit Bug' : 'Report New Bug'}</h2>
            <BugForm
              onSubmit={editingBug ? 
                (data) => handleUpdateBug(editingBug._id, data) : 
                handleCreateBug
              }
              initialData={editingBug || {}}
              isEditing={!!editingBug}
              onCancel={handleCancelForm}
            />
          </div>
        ) : (
          <div className="list-section">
            <h2>Bug Reports ({bugs.length})</h2>
            {loading ? (
              <div className="loading">Loading bugs...</div>
            ) : (
              <>
                <BugList
                  bugs={bugs}
                  onEdit={(bug) => {
                    console.log('‚úèÔ∏è Editing bug:', bug);
                    setEditingBug(bug);
                    setShowForm(true);
                  }}
                  onDelete={(bugId) => {
                    console.log('üóëÔ∏è Deleting bug:', bugId);
                    handleDeleteBug(bugId);
                  }}
                />
                {/* Debug info */}
                <div style={{ marginTop: '20px', padding: '10px', background: '#f5f5f5', borderRadius: '4px' }}>
                  <small>
                    <strong>Debug Info:</strong> {bugs.length} bugs loaded | 
                    API Connected: {bugs.length > 0 ? '‚úÖ' : '‚ùå'} |
                    Last updated: {new Date().toLocaleTimeString()}
                  </small>
                </div>
              </>
            )}
          </div>
        )}
      </main>
    </div>
  );
}

export default BugTracker;
