import React, { useState } from 'react';
import Button from './Button';

const BugForm = ({ onSubmit, initialData = {}, isEditing = false, onCancel }) => {
  const [formData, setFormData] = useState({
    title: initialData.title || '',
    description: initialData.description || '',
    priority: initialData.priority || 'medium',
    status: initialData.status || 'open',
    reporter: initialData.reporter || '',
    assignee: initialData.assignee || '',
    stepsToReproduce: initialData.stepsToReproduce || [''],
    environment: initialData.environment || {
      os: '',
      browser: '',
      version: ''
    }
  });
  
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleStepChange = (index, value) => {
    const newSteps = [...formData.stepsToReproduce];
    newSteps[index] = value;
    setFormData(prev => ({
      ...prev,
      stepsToReproduce: newSteps
    }));
  };

  const addStep = () => {
    setFormData(prev => ({
      ...prev,
      stepsToReproduce: [...prev.stepsToReproduce, '']
    }));
  };

  const removeStep = (index) => {
    if (formData.stepsToReproduce.length > 1) {
      const newSteps = formData.stepsToReproduce.filter((_, i) => i !== index);
      setFormData(prev => ({
        ...prev,
        stepsToReproduce: newSteps
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }
    
    if (!formData.description.trim()) {
      newErrors.description = 'Description is required';
    }
    
    if (!formData.reporter.trim()) {
      newErrors.reporter = 'Reporter name is required';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setIsSubmitting(true);
    
    try {
      // Filter out empty steps and ensure it's always an array
      const filteredSteps = formData.stepsToReproduce
        .filter(step => step.trim() !== '');
      
      const submitData = {
        ...formData,
        stepsToReproduce: filteredSteps.length ? filteredSteps : []
      };
      
      await onSubmit(submitData);
    } catch (error) {
      console.error('Error submitting form:', error);
      setErrors({ submit: error.message || 'Failed to submit bug report' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCancel = () => {
    if (onCancel) {
      onCancel();
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bug-form">
      <div className="form-group">
        <label htmlFor="title">Title *</label>
        <input
          type="text"
          id="title"
          name="title"
          value={formData.title}
          onChange={handleChange}
          className={errors.title ? 'error' : ''}
          placeholder="Enter bug title"
        />
        {errors.title && <span className="error-message">{errors.title}</span>}
      </div>

      <div className="form-group">
        <label htmlFor="description">Description *</label>
        <textarea
          id="description"
          name="description"
          value={formData.description}
          onChange={handleChange}
          rows="4"
          className={errors.description ? 'error' : ''}
          placeholder="Describe the bug in detail"
        />
        {errors.description && <span className="error-message">{errors.description}</span>}
      </div>

      <div className="form-row">
        <div className="form-group">
          <label htmlFor="priority">Priority</label>
          <select
            id="priority"
            name="priority"
            value={formData.priority}
            onChange={handleChange}
          >
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
            <option value="critical">Critical</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="status">Status</label>
          <select
            id="status"
            name="status"
            value={formData.status}
            onChange={handleChange}
          >
            <option value="open">Open</option>
            <option value="in-progress">In Progress</option>
            <option value="resolved">Resolved</option>
            <option value="closed">Closed</option>
          </select>
        </div>
      </div>

      <div className="form-row">
        <div className="form-group">
          <label htmlFor="reporter">Reporter *</label>
          <input
            type="text"
            id="reporter"
            name="reporter"
            value={formData.reporter}
            onChange={handleChange}
            className={errors.reporter ? 'error' : ''}
            placeholder="Your name"
          />
          {errors.reporter && <span className="error-message">{errors.reporter}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="assignee">Assignee</label>
          <input
            type="text"
            id="assignee"
            name="assignee"
            value={formData.assignee}
            onChange={handleChange}
            placeholder="Assign to (optional)"
          />
        </div>
      </div>

      <div className="form-group">
        <label>Steps to Reproduce</label>
        {formData.stepsToReproduce.map((step, index) => (
          <div key={index} className="step-input">
            <input
              type="text"
              value={step}
              onChange={(e) => handleStepChange(index, e.target.value)}
              placeholder={`Step ${index + 1}`}
            />
            {formData.stepsToReproduce.length > 1 && (
              <Button
                type="button"
                variant="danger"
                size="sm"
                onClick={() => removeStep(index)}
              >
                Remove
              </Button>
            )}
          </div>
        ))}
        <Button type="button" variant="secondary" onClick={addStep}>
          Add Step
        </Button>
      </div>

      <div className="form-group">
        <label>Environment (Optional)</label>
        <div className="environment-fields">
          <input
            type="text"
            name="environment.os"
            value={formData.environment.os}
            onChange={(e) => setFormData(prev => ({
              ...prev,
              environment: { ...prev.environment, os: e.target.value }
            }))}
            placeholder="Operating System"
          />
          <input
            type="text"
            name="environment.browser"
            value={formData.environment.browser}
            onChange={(e) => setFormData(prev => ({
              ...prev,
              environment: { ...prev.environment, browser: e.target.value }
            }))}
            placeholder="Browser"
          />
          <input
            type="text"
            name="environment.version"
            value={formData.environment.version}
            onChange={(e) => setFormData(prev => ({
              ...prev,
              environment: { ...prev.environment, version: e.target.value }
            }))}
            placeholder="Version"
          />
        </div>
      </div>

      {errors.submit && (
        <div className="error-message submit-error">{errors.submit}</div>
      )}

      <div className="form-actions">
        <Button 
          type="button" 
          variant="secondary"
          onClick={handleCancel}
          disabled={isSubmitting}
        >
          Cancel
        </Button>
        <Button 
          type="submit" 
          variant="primary"
          disabled={isSubmitting}
          className="submit-btn"
        >
          {isSubmitting ? 'Submitting...' : isEditing ? 'Update Bug' : 'Report Bug'}
        </Button>
      </div>
    </form>
  );
};

export default BugForm;
