import { useState, useEffect } from 'react';
import { getBugs, createBug } from '../services/bugService';
import BugForm from './BugForm';
import BugList from './BugList';
import BugFilters from './BugFilters';

const BugTracker = () => {
  // Make sure ALL state variables are defined
  const [bugs, setBugs] = useState([]);
  const [showForm, setShowForm] = useState(false); // This was missing!
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [apiConnected, setApiConnected] = useState(false);

  // Load bugs on component mount
  useEffect(() => {
    console.log('ðŸ”„ BugTracker mounted, loading bugs...');
    loadBugs();
  }, []);

  const loadBugs = async () => {
    console.log('ðŸ“¡ Fetching bugs from API...');
    try {
      setLoading(true);
      const bugsData = await getBugs();
      setBugs(bugsData);
      setApiConnected(true);
      console.log('âœ… API Response:', bugsData);
      console.log('ðŸ› Bugs array:', bugsData);
    } catch (err) {
      console.error('âŒ Error loading bugs:', err);
      setError('Failed to load bugs');
      setApiConnected(false);
    } finally {
      setLoading(false);
      console.log('ðŸ Loading completed');
    }
  };

  // Define handleCreateBug function
  const handleCreateBug = async (bugData) => {
    console.log('ðŸ“ Creating bug:', bugData);
    try {
      setLoading(true);
      const newBug = await createBug(bugData);
      setBugs(prev => [...prev, newBug]);
      setShowForm(false);
      setError('');
      console.log('âœ… Bug created successfully:', newBug);
      return newBug;
    } catch (err) {
      console.error('âŒ Error creating bug:', err);
      setError(err.message || 'Failed to create bug');
      throw new Error('Failed to create bug. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleRefresh = () => {
    console.log('ðŸ”„ Refreshing bugs...');
    setError('');
    loadBugs();
  };

  const toggleForm = () => {
    const newShowForm = !showForm;
    console.log('ðŸ“ Toggling form, current showForm:', newShowForm);
    setShowForm(newShowForm);
    setError('');
  };

  const handleFilterByStatus = () => {
    console.log('Filter by status');
    // Implement status filtering logic
  };

  const handleFilterByPriority = () => {
    console.log('Filter by priority');
    // Implement priority filtering logic
  };

  return (
    <div className="bug-tracker">
      <header className="bug-tracker-header">
        <div className="header-content">
          <div className="logo">
            <i className="fas fa-bug"></i>
            <div>
              <h1>MERN Bug Tracker</h1>
              <div className="tagline">Track, manage, and resolve bugs efficiently</div>
            </div>
          </div>
        </div>
        
        <div className="actions">
          <button className="btn btn-primary" onClick={toggleForm}>
            <i className="fas fa-plus-circle"></i>
            Report New Bug
          </button>
          <button className="btn btn-secondary" onClick={handleRefresh} disabled={loading}>
            <i className="fas fa-sync-alt"></i>
            {loading ? 'Refreshing...' : 'Refresh'}
          </button>
          
          <div className="filters">
            <button className="filter-btn" onClick={handleFilterByStatus}>
              <i className="fas fa-filter"></i>
              Filter by Status
            </button>
            <button className="filter-btn" onClick={handleFilterByPriority}>
              <i className="fas fa-sort-amount-down"></i>
              Filter by Priority
            </button>
          </div>
        </div>
      </header>
      
      <div className="main-content">
        <div className="section-header">
          <h2 className="section-title">
            Bug Reports <span className="bug-count">{bugs.length}</span>
          </h2>
        </div>
        
        {error && (
          <div className="error-message">
            <i className="fas fa-exclamation-triangle"></i>
            {error}
          </div>
        )}
        
        {showForm && (
          <BugForm 
            onSubmit={handleCreateBug}
            onCancel={() => setShowForm(false)}
            loading={loading}
          />
        )}
        
        {bugs.length === 0 && !showForm ? (
          <div className="empty-state">
            <i className="fas fa-bug"></i>
            <h3>No bugs reported yet</h3>
            <p>Be the first to report a bug and help improve the application!</p>
          </div>
        ) : (
          <BugList bugs={bugs} loading={loading} />
        )}
        
        <div className="debug-info">
          <div>
            <strong>Debug Info:</strong> {bugs.length} bugs loaded | Last updated: {new Date().toLocaleTimeString()}
          </div>
          <div className={`status ${apiConnected ? 'connected' : 'disconnected'}`}>
            <i className={`fas ${apiConnected ? 'fa-check-circle' : 'fa-times-circle'}`}></i>
            API Connected: {apiConnected ? 'âœ“' : 'âœ—'}
          </div>
        </div>
      </div>
    </div>
  );
};

export default BugTracker;
